/* -------------------------------------------------- PREFERENCES */
:root {
  --card-max-width: 40em;
  --card-text-align: left;
  --font-size-regular: 16px;
  --font-size-small: 14px;
  --font-family: "Inter", -apple-system, sytem-ui, BlinkMacSystemFont, Segoe UI,
    Roboto, Helvetica, Arial, sans-serif;
  --img-width: 100%;
  --img-brightness: 0.9;
  --img-filter: none;
}

/* -------------------------------------------------- COLORS */
.card,
.card.night_mode {
  /* Dark theme */
  background-color: #242426;
  --text-fg: #ffffffe6;
  --text-fg-faint: #ffffffb3;
  --text-bg-selected: #ffffff1f;
  --card-bg: #2e2f31;
  --card-border: #ffffff0a;
  --card-box-shadow: #0000001f;
  --divider: #ffffff1f;
  --tag-fg: #ffffffb3;
  --tag-bg: #ffffff14;
  --tag-fg-active: #ffffff;
  --tag-bg-active: #ffffff1f;
  --tag-border: transparent;
  --cloze-fg: #B39DDB;
  --cloze-bg: transparent;
  --link-fg: #5da3d5;
  --link-bg: transparent;
  --link-fg-active: #71afda;
  --link-bg-active: transparent;
  --bold-fg: #90CAF9;
  --italic-fg: #FFF176;
  --bold-italic-fg: #4DB6AC;
  --underline-fg: #E57373;
  --slides-button-fg: #ffffffe0;
  --slides-button-bg: #7E57C2;
  --slides-button-fg-active: #FFFFFFe0;
  --slides-button-bg-active: #7E57C260;
  --slides-button-border: #FFFFFF30;
  --lo-button-fg: #ffffffe0;
  --lo-button-bg: #4DB6AC;
  --lo-button-fg-active: #FFFFFFe0;
  --lo-button-bg-active: #4DB6AC60;
  --lo-button-border: #ffffff20;
  --video-button-fg: #ffffffe0;
  --video-button-bg: #E57373;
  --video-button-fg-active: #FFFFFFe0;
  --video-button-bg-active: #E5737360;
  --video-button-border: #ffffff20;
}

/* -------------------------------------------------- BACKGROUND */
.card {
  cursor: default;
  padding: 0.5em 0;
}

html:not(.mobile) .card {
  padding: 0.5em;
}

.card::-webkit-scrollbar {
  display: none;
}

/* -------------------------------------------------- FLASHCARD */
.prettify-flashcard {
  background-color: var(--card-bg);
  border-radius: 0.25em;
  border: 1px solid var(--card-border);
  box-shadow: var(--card-box-shadow) 0px 7px 14px 0px, var(--card-box-shadow) 0px 3px 6px 0px;
  color: var(--text-fg);
  font-family: var(--font-family);
  font-size: var(--font-size-regular);
  line-height: 1.5;
  margin: 0 auto;
  max-width: var(--card-max-width);
  text-align: var(--card-text-align);
  word-wrap: break-word;
	position: relative;
}

.prettify-flashcard ::selection {
  background-color: var(--text-bg-selected);
}

/* -------------------------------------------------- FIELDS */
.prettify-field {
  margin: 2em;
}

.mobile .prettify-field {
  margin: 1em;
}

.prettify-field--back {
  color: var(--text-fg-faint);
  font-size: var(--font-size-small);
}

/* -------------------------------------------------- CLOZE */
.cloze {
  background-color: var(--cloze-bg);
  color: var(--cloze-fg);
  font-weight: bold;
}

.cloze-inactive {
  background-color: var(--cloze-bg);
  color: var(--bold-fg);
  font-weight: bold;
}

/* -------------------------------------------------- DECK */
.prettify-deck {
  margin: 2em;
  display: flex;
  color: var(--text-fg-faint);
  font-size: var(--font-size-small);
  white-space: nowrap;
}

.mobile .prettify-deck {
  margin: 1em;
}

.prettify-subdeck {
  text-decoration: underline;
  text-overflow: ellipsis;
  overflow: hidden;
}

/* -------------------------------------------------- TAGS */
.prettify-tags {
  margin: 2em;
  display: flex;
  flex-flow: row wrap;
  font-size: var(--font-size-small);
}

.mobile .prettify-tags {
  margin: 1em;
}

.prettify-tag {
  all: initial;
  background-color: var(--tag-bg);
  border-radius: 0.2em;
  color: var(--tag-fg);
  display: inline;
  font-size: var(--font-size-small);
  font-family: var(--font-family);
  margin: 0 0.5em 0.5em 0;
  padding: 0.25em;
  transition: all 0.25s;
  word-break: break-word;
}

.prettify-tag:hover {
  background-color: var(--tag-bg-active);
  color: var(--tag-fg-active);
  cursor: pointer;
}

/* -------------------------------------------------- DIVIDER */
.prettify-divider {
  background-color: transparent;
  border: none;
  border-bottom: 1px dashed var(--divider);
  margin: 1em;
  padding: 0;
}

.prettify-divider--answer {
  margin: 0 0 1em;
}

/* -------------------------------------------------- IMAGES */
img {
  border-radius: 0.25em;
  display: block;
  margin: 1em auto;
  max-width: var(--img-width) !important;
}

.prettify-flashcard img {
  filter: var(--img-filter);
  opacity: var(--img-brightness);
}

.prettify-flashcard img:hover {
  cursor: zoom-in;
  filter: none;
  max-width: 100% !important;
  opacity: 1;
}

img+br {
  display: none;
}

#logo {
  margin: 0 auto;
  max-width: var(--card-max-width);
}

#logo svg {
  display: block;
  margin: 1em auto;
  max-width: 225px !important;
  color: var(--card-bg);
  filter: drop-shadow(2px 2px 3px #00000040);
  fill: var(--card-bg);
	stroke: var(--card-border);
	stroke-width: 1px;
}

#image-box {
  display: none;
  position: fixed;
  z-index: 9999;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: #00000070;
  box-sizing: border-box;
  padding: 50px;
}

#image-box:hover {
  cursor: zoom-out;
}

#image-box-image {
  width: 100%;
  height: 100%;
  background-size: contain;
  background-repeat: no-repeat no-repeat;
  background-position: center center;
}

/* -------------------------------------------------- TABLES */
table {
  border-collapse: separate;
  border-spacing: 0;
  margin: 0 auto;
  max-width: 100%;
}

table thead {
  background-color: var(--card-border);
}

table tr:nth-of-type(even) {
  background-color: var(--card-border);
}

table tr:first-child th:first-child {
  border-top-left-radius: 0.25em;
}

table tr:first-child th:last-child {
  border-top-right-radius: 0.25em;
}

table tr:last-child td:first-child {
  border-bottom-left-radius: 0.25em;
}

table tr:last-child td:last-child {
  border-bottom-right-radius: 0.25em;
}

table th {
  border-bottom: solid 1px var(--text-bg-selected);
  border-left: solid 1px var(--text-bg-selected);
  border-top: solid 1px var(--text-bg-selected);
  padding: 0.5em;
}

table th:last-child {
  border-right: solid 1px var(--text-bg-selected);
}

table td {
  border-bottom: solid 1px var(--text-bg-selected);
  border-left: solid 1px var(--text-bg-selected);
  padding: 0.5em;
}

table td:last-of-type {
  border-right: solid 1px var(--text-bg-selected);
}

/* -------------------------------------------------- HYPERLINKS */
a,
a:visited {
  text-decoration: none;
  color: var(--link-fg);
}

a:hover,
a:active {
  text-decoration: underline;
  color: var(--link-fg-active);
}

/* -------------------------------------------------- FORMATTING */
b {
	font-weight: bold;
  color: var(--bold-fg);
}

i {
	font-weight: bold;
  color: var(--italic-fg);
}

b>i,
i>b {
	font-weight: bold;
  color: var(--bold-italic-fg);
}

u {
	font-weight: bold;
  color: var(--underline-fg);
}

pre {
  white-space: normal;
}

/* -------------------------------------------------- CUSTOM FONTS */
@font-face {
  font-family: Inter;
  src: local("Inter-Regular"), url("_Inter-Regular.woff2") format("woff2");
  font-style: normal;
  font-weight: normal;
}

@font-face {
  font-family: Inter;
  src: local("Inter-Bold"), url("_Inter-Bold.woff2") format("wofff2");
  font-style: normal;
  font-weight: bold;
}

@font-face {
  font-family: Inter;
  src: local("Inter-Italic"), url("_Inter-Italic.woff2") format("wofff2");
  font-style: italic;
  font-weight: normal;
}

@font-face {
  font-family: Inter;
  src: local("Inter-BoldItalic"), url("_Inter-BoldItalic.woff2") format("wofff2");
  font-style: italic;
  font-weight: bold;
}

/* -------------------------------------------------- Slides Drawer */
prettify-field.drawerButtons {
	width: 100%;
	text-align: center;
}

#drawerButton-slides {
  all: initial;
  border-radius: 0.2em;
  display: inline;
  font-size: var(--font-size-small);
  font-family: var(--font-family);
  margin: 0 0.5em 0.5em 0;
  padding: 0.5em;
  transition: all 0.25s;
  word-break: break-word;
  background-color: var(--slides-button-bg);
  color: var(--slides-button-fg);
  border: 1px solid var(--slides-button-border);
}

#drawerButton-slides:hover {
  cursor: pointer;
}

#drawerButton-slides.active {
  background-color: var(--slides-button-bg-active) !important;
  color: var(--slides-button-fg-active) !important;
}

#drawerButton-lo {
  all: initial;
  border-radius: 0.2em;
  display: inline;
  font-size: var(--font-size-small);
  font-family: var(--font-family);
  margin: 0 0.5em 0.5em 0;
  padding: 0.5em;
  transition: all 0.25s;
  word-break: break-word;
  background-color: var(--lo-button-bg);
  color: var(--lo-button-fg);
  border: 1px solid var(--lo-button-border);
}

#drawerButton-lo:hover {
  cursor: pointer;
}

#drawerButton-lo.active {
  background-color: var(--lo-button-bg-active) !important;
  color: var(--lo-button-fg-active) !important;
}

#drawerButton-video {
  all: initial;
  border-radius: 0.2em;
  display: inline;
  font-size: var(--font-size-small);
  font-family: var(--font-family);
  margin: 0 0.5em 0.5em 0;
  padding: 0.5em;
  transition: all 0.25s;
  word-break: break-word;
  background-color: var(--video-button-bg);
  color: var(--video-button-fg);
  border: 1px solid var(--video-button-border);
}

#drawerButton-video:hover {
  cursor: pointer;
	background-color: var(--video-button-bg-active) !important;
  color: var(--video-button-fg-active) !important;
}

/* -------------------------------------------------- GEAUX TIGERS */

.avfElement {
	transition: 0.75s linear;
}

.avfElement.screenGlow {
	display: none;
}

/* -------------------------------------------------- Logo Effects */

#logo .avfElement.again {
  transition: 0.15s linear;
  fill: #EF5350;
}

#logo .avfElement.hard {
  transition: 0.15s linear;
  fill: #FFEA00;
}

#logo .avfElement.good {
  transition: 0.15s linear;
  fill: #00E676;
}

#logo .avfElement.easy {
  transition: 0.15s linear;
  fill: #2979FF;
}

/* -------------------------------------------------- Card Effects */

.prettify-flashcard.avfElement.again {
	transition: 0.15s linear;
	filter: drop-shadow(0px 0px 50px #EF5350A0);
}

.prettify-flashcard.avfElement.hard {
	transition: 0.15s linear;
	filter: drop-shadow(0px 0px 50px #FFEA00A0);
}

.prettify-flashcard.avfElement.good {
	transition: 0.15s linear;
	filter: drop-shadow(0px 0px 50px #00E676A0);
}

.prettify-flashcard.avfElement.easy {
	transition: 0.15s linear;
	filter: drop-shadow(0px 0px 50px #2979FFA0);
}

/* GENERAL CARD STYLE */
.card,
.card.nightMode {
  font-family: "Inter", -apple-system, sytem-ui, BlinkMacSystemFont, Segoe UI,
    Roboto, Helvetica, Arial, sans-serif;
  font-size: 150%;
  text-align: center;
  background-color: #242426;
  color: #ffffffe6;
  --bold-fg: #90CAF9;
  --italic-fg: #FFF176;
  --bold-italic-fg: #4DB6AC;
  --underline-fg: #E57373;
}

b,
strong {
  color: var(--bold-fg);
}

i,
em {
  color: var(--italic-fg);
}

b>i,
i>b {
  color: var(--bold-italic-fg);
}

u {
  color: var(--underline-fg);
}

pre {
  white-space: normal;
}

/* OCCLUSION CSS START - don't edit this */
#io-overlay {
  position: absolute;
  top: 0;
  width: 100%;
  z-index: 3
}

#io-original {
  position: relative;
  top: 0;
  width: 100%;
  z-index: 2;
  visibility: hidden;
}

#io-wrapper {
  position: relative;
  width: 100%;
}

/* OCCLUSION CSS END */

/* OTHER STYLES */
#io-header {
  font-size: 1.1em;
  margin-bottom: 0.2em;
}

#io-footer {
  max-width: 80%;
  margin-left: auto;
  margin-right: auto;
  margin-top: 0.8em;
  font-style: italic;
}

#io-extra-wrapper {
  /* the wrapper is needed to center the
  left-aligned blocks below it */
  width: 80%;
  margin-left: auto;
  margin-right: auto;
  margin-top: 0.5em;
}

#io-extra {
  text-align: center;
  display: inline-block;
}

.io-extra-entry {
  margin-top: 0.8em;
  font-size: 0.9em;
  text-align: left;
}

.io-field-descr {
  margin-bottom: 0.2em;
  font-weight: bold;
  font-size: 1em;
}

#io-revl-btn {
  font-size: 0.5em;
}

/* ADJUSTMENTS FOR MOBILE DEVICES */

.mobile .card,
.mobile #content {
  font-size: 120%;
  margin: 0;
}

.mobile #io-extra-wrapper {
  width: 95%;
}

.mobile #io-revl-btn {
  font-size: 0.8em;
}

tmpls.afmt:
{{#Image}}
<div id="io-header">{{Header}}</div>
<div id="io-wrapper">
  <div id="io-overlay">{{Answer Mask}}</div>
  <div id="io-original">{{Image}}</div>
</div>
{{#Footer}}<div id="io-footer">{{Footer}}</div>{{/Footer}}
<button id="io-revl-btn" onclick="toggle();" accesskey="m">Toggle Masks</button>
<div id="io-extra-wrapper">
  <div id="io-extra">
    {{#Remarks}}
    <div class="io-extra-entry">
      <div class="io-field-descr">Remarks</div>{{Remarks}}
    </div>
    {{/Remarks}}
    {{#Sources}}
    <div class="io-extra-entry">
      <div class="io-field-descr">Sources</div>{{Sources}}
    </div>
    {{/Sources}}
    {{#Extra 1}}
    <div class="io-extra-entry">
      <div class="io-field-descr">Extra 1</div>{{Extra 1}}
    </div>
    {{/Extra 1}}
    {{#Extra 2}}
    <div class="io-extra-entry">
      <div class="io-field-descr">Extra 2</div>{{Extra 2}}
    </div>
    {{/Extra 2}}
  </div>
</div>
<script>
  var toggle = function () {
    var amask = document.getElementById('io-overlay');
    if (amask.style.display === 'block' || amask.style.display === '')
      amask.style.display = 'none';
    else
      amask.style.display = 'block'
  }
  function handleIOEHotKeys(e) {
    if (e.key === "`") {
      toggle();
    }
  }
  if (!globalThis.IOEhotKeyHandler) {
    document.addEventListener("keyup", handleIOEHotKeys);
    globalThis.IOEhotKeyHandler = handleIOEHotKeys;
  }
  aFade = 50, qFade = 0;
  var mask = document.querySelector('#io-overlay>img');
  function loaded() {
    var original = document.querySelector('#io-original');
    original.style.visibility = "visible";
  }
  if (mask === null || mask.complete) {
    loaded();
  } else {
    mask.addEventListener('load', loaded);
  }
</script>
{{/Image}}

